import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import RegisterForm from './UserComponents/RegisterForm';
import LoginForm from './UserComponents/LoginForm';

const WelcomePage = () => {
    const [showRegisterForm, setShowRegisterForm] = useState(false);
    const [showLoginForm, setShowLoginForm] = useState(false);
    const [registrationSuccess, setRegistrationSuccess] = useState(false);
  
    const history = useHistory();
  
    const handleRegisterClick = () => {
      setShowRegisterForm(true);
      setShowLoginForm(false);
      setRegistrationSuccess(false); // Reset registration success message
    };
  
    const handleLoginClick = () => {
      setShowRegisterForm(false);
      setShowLoginForm(true);
    };
  
    const handleRegistrationSuccess = () => {
      setRegistrationSuccess(true);
      setShowRegisterForm(false);
      setShowLoginForm(false);
    };
  
    return (
      <div>
        {registrationSuccess && (
          <div>
            <p>Welcome Cristina, your registration was successful. You can now add cash and start trading stocks.</p>
          </div>
        )}
  
        {!showRegisterForm && !showLoginForm && !registrationSuccess && (
          <div>
            <p>Register or login to access the stock trading system.</p>
            <button onClick={handleRegisterClick}>Register</button>
            <button onClick={handleLoginClick}>Login</button>
          </div>
        )}
  
        {showRegisterForm && (
          <div>
            <h2>Register</h2>
            <RegisterForm onRegister={handleRegistrationSuccess} />
            <p>Already have an account? <button onClick={() => setShowRegisterForm(false)}>Go to Login</button></p>
          </div>
        )}
  
        {showLoginForm && (
          <div>
            <h2>Login</h2>
            <LoginForm />
            <p>Don't have an account? <button onClick={() => setShowLoginForm(false)}>Go to Register</button></p>
          </div>
        )}
      </div>
    );
  };
  
  export default WelcomePage;
