import React, { useEffect, useState } from 'react';
import { Typography, Paper, Tabs, Tab, List, ListItem, ListItemText } from '@mui/material';

const Dashboard = ({ userId, userFullName }) => {
  const [portfolio, setPortfolio] = useState([]);
  const [cashBalance, setCashBalance] = useState(10000);
  const [transactionHistory, setTransactionHistory] = useState([]);
  const [selectedTab, setSelectedTab] = useState(0);

//   useEffect(() => {
//     // Fetch user data from the backend when the component mounts
//     const fetchData = async () => {
//       try {
//         // Fetch portfolio data
//         const portfolioResponse = await fetch(`/api/users/${userId}/portfolio`);
//         const portfolioData = await portfolioResponse.json();
//         setPortfolio(portfolioData);

//         // Fetch cash balance
//         const cashBalanceResponse = await fetch(`/api/users/${userId}/cash-balance`);
//         const cashBalanceData = await cashBalanceResponse.json();
//         setCashBalance(cashBalanceData.balance);

//         // Fetch transaction history
//         const transactionHistoryResponse = await fetch(`/api/users/${userId}/transactions`);
//         const transactionHistoryData = await transactionHistoryResponse.json();
//         setTransactionHistory(transactionHistoryData);
//       } catch (error) {
//         console.error('Error fetching user data:', error);
//       }
//     };

//     fetchData();
//   }, [userId]);

useEffect(() => {
    // Simulated data for stocks
    const fakeStocks = [
      { symbol: 'AAPL', quantity: 10 },
      { symbol: 'GOOGL', quantity: 5 },
      { symbol: 'MSFT', quantity: 8 },
    ];

    // Simulated data for transaction history
    const fakeTransactions = [
      { id: 1, type: 'BUY', symbol: 'AAPL', quantity: 5, price: 150 },
      { id: 2, type: 'SELL', symbol: 'GOOGL', quantity: 2, price: 2000 },
      { id: 3, type: 'BUY', symbol: 'MSFT', quantity: 3, price: 180 },
    ];

    // Set fake data to state
    setPortfolio(fakeStocks);
    setTransactionHistory(fakeTransactions);
  }, [userId]);

  const handleChangeTab = (event, newValue) => {
    setSelectedTab(newValue);
  };

  return (
    <div>
      <Typography variant="h4" gutterBottom>
        Welcome {userFullName}
      </Typography>

      <Paper elevation={3} style={{ marginBottom: '16px' }}>
        <Tabs value={selectedTab} onChange={handleChangeTab} centered>
          <Tab label="Portfolio" />
          <Tab label="Cash Balance" />
          <Tab label="Transaction History" />
        </Tabs>
        <div style={{ padding: '16px' }}>
          {selectedTab === 0 && (
            <div>
              <Typography variant="h6" gutterBottom>
                Portfolio
              </Typography>
              <List>
                {portfolio.map((stock) => (
                  <ListItem key={stock.symbol}>
                    <ListItemText primary={`${stock.symbol} - ${stock.quantity} shares`} />
                  </ListItem>
                ))}
              </List>
            </div>
          )}
          {selectedTab === 1 && (
            <div>
              <Typography variant="h6" gutterBottom>
                Cash Balance
              </Typography>
              <Typography>${cashBalance}</Typography>
            </div>
          )}
          {selectedTab === 2 && (
            <div>
              <Typography variant="h6" gutterBottom>
                Transaction History
              </Typography>
              <List>
                {transactionHistory.map((transaction) => (
                  <ListItem key={transaction.id}>
                    <ListItemText
                      primary={`${transaction.type === 'BUY' ? 'Bought' : 'Sold'} ${transaction.quantity} shares of ${transaction.symbol} for $${transaction.price} each`}
                    />
                  </ListItem>
                ))}
              </List>
            </div>
          )}
        </div>
      </Paper>
    </div>
  );
};

export default Dashboard;
